const cartButtons=document.querySelectorAll(".catalog-button-buy"),cartPopup=document.querySelector(".modal-cart"),cartClose=cartPopup.querySelector(".modal-close");for(cartButton of cartButtons)cartButton.addEventListener("click",function(t){t.preventDefault(),cartPopup.classList.add("modal-display")});cartClose.addEventListener("click",function(t){t.preventDefault(),cartPopup.classList.remove("modal-display")}),window.addEventListener("keydown",function(t){27===t.keyCode&&cartPopup.classList.contains("modal-display")&&(t.preventDefault(),cartPopup.classList.remove("modal-display"))});